
function stopPropagation(ev){if(typeof ev.preventDefault==="function"){ev.preventDefault();}
if(typeof ev.stopPropagation==="function"){ev.stopPropagation();}
else{ev.cancelBubble=true;}}
function returnFalse(){return false;}
function onClickFila(e){if(!e)var e=window.event;var src=(e.target)||event.srcElement;if(!src)return null;var tr=null;if(src.tagName==="TR"){tr=src;}
else if(src.tagName==="TD"){tr=src.parentNode;}
else if((src.parentNode)&&(src.parentNode.tagName==="TD")){tr=src.parentNode.parentNode;}
if(!tr){return false;}
var inputs=tr.getElementsByTagName("input");if((inputs)&&(inputs.length)&&(inputs.length>0)){for(var i=0;(i<inputs.length);i++){var el=inputs[i];if((/submit/i).test(el.type)){return el;}}}}
function isInputSubmit(el){return(el)&&((/^input$/i).test(el.tagName))&&((/^submit$/i).test(el.type));}
function isAButton(el){return(el)&&(el.tagName==="A")&&(el.getAttribute("role"))&&(el.getAttribute("role")==="button");}
function cursorEnEspera(){if(!/en-espera/.test(document.body.className)){document.body.className+=" en-espera";}}
function deshabilitarTodosEnviadores(){var lst=document.getElementsByTagName("input");if(lst){var el;for(var i=0;(i<lst.length);i++){el=lst[i];if((isInputSubmit(el))&&(!el.disabled)){if(el.lafe_procesando){el.className+=" deshabilitado-clic";}
else{el.className+=" deshabilitado";el.disabled=true;}}}}
lst=document.getElementsByTagName("a");if(lst){var el;for(var i=0;(i<lst.length);i++){el=lst[i];if(isAButton(el)){el.onclick=returnFalse;if(el.lafe_procesando){el.className+=" deshabilitado-clic";}
else{el.className+=" deshabilitado";}}}}}
function onClickAButton(e){if(!e)var e=window.event;var a=e.target||e.srcElement;if(isAButton(a)){if(!a.lafe_procesando){ponerPaginaEnProcesando(a);if(a.getAttribute("data-lafe-texto-procesando")){a.innerHTML=a.getAttribute("data-lafe-texto-procesando");}}
else{stopPropagation(e);return false;}}}
function onClickInputSubmit(e,el){if(!e)var e=window.event;var input=(el)?el:e.target||e.srcElement;if(isInputSubmit(input)){var f=input.form;for(var i=0;(i<f.elements.length);i++){var fel;fel=f[i];if(isInputSubmit(fel)){fel.lafe_clicked=false;}}
input.lafe_clicked=true;if(el){input.click();}
else{if(input.lafe_procesando){stopPropagation(e);return false;}}}}
function onSubmitForm(e){if(!e)var e=window.event;var f=this;var i,el,clickedInput=null;if(f.elements){for(i=0;(i<f.elements.length);i++){el=f.elements[i];if(isInputSubmit(el)){if(el.lafe_clicked){clickedInput=el;}}}}
else{clickedInput=this;}
if(f.target==="_blank"){if((clickedInput)&&(clickedInput.className==="pdf")&&(clickedInput.id)){el=document.getElementById(clickedInput.id+"-ayuda");if(el){el.style.display="block";}}
return true;}
ponerPaginaEnProcesando(clickedInput);}
function ponerPaginaEnProcesando(el){if(el){el.lafe_procesando=true;cursorEnEspera();deshabilitarTodosEnviadores();}}
function setOnSubmitTodosForms(){if(document.forms){for(var i=0;(i<document.forms.length);i++){document.forms[i].onsubmit=onSubmitForm;}}}